INSERT INTO games (id, name, summary, release) VALUES (18355, 'Climberia', 'Climberia is an action mobile game with amazing climbing gameplay. Players control both hands of a legless pirate using inertia to climb up and collect gold while running away from a greedy octopus. Fun yet challenging, be warned - you''ll die a lot.', DATE('2016-04-04')) ON CONFLICT DO NOTHING;
