INSERT INTO games (id, name, summary, release) VALUES (649, 'Command & Conquer: Generals', 'A new chapter in the C&amp;C universe set somewhere 20 years ahead from the real world, which pit 3 sides for global domination. Players will be able to select from 3 sides which are US forces, Chinese forces and the GLA (Global Liberation Army).', DATE('2003-02-09')) ON CONFLICT DO NOTHING;
