INSERT INTO games (id, name, summary, release) VALUES (24445, 'Conga Master', null, DATE('2016-09-13')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17729, 'Congo', 'Stuck deep in the Congo jungle, you and your teammates are hunted by packs of demons lurking in the darkest areas. Light is your friend in this hostile environment. Scavenge equipment and utilize good teamwork to secure your route of escape in this top-down co-op horror game.', DATE('2014-10-03')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5669, 'Congo Bongo', null, DATE('1983-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16107, 'Coniclysm', 'Coniclysm is an abstract third person shooter where the player assumes the role of an intelligence adapting for survival in a strange new universe.Key Features
INSERT INTO games (id, name, summary, release) VALUES (1286, 'Conker''s Bad Fur Day', 'Humorous action-platformer that does away with the tedious item collection found in most games in the genre. Instead, BFD employs a combination of standard jump, run and explore mechanics and context-sensitive gags and actions. For instance, in the beginning of the game, by pressing the B button on the first pad he encounters, Conker drinks some Alka-Seltzer to wipe out his hangover, at which point players can proceed forward. Later on these pads are used to activate a slingshot and throwing knives; to turn Conker into an anvil and drop downward; to shoot automatic, double-handed guns; to activate a The Matrix-inspired slow-motion effect and flip through the air shooting enemies; and much, much more.', DATE('2001-03-04')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1424, 'Conker''s Pocket Tales', null, DATE('1999-06-07')) ON CONFLICT DO NOTHING;
