INSERT INTO games (id, name, summary, release) VALUES (14400, 'Cosmic DJ', 'Cosmic DJ is a quick, "pick-up and play" musical experience about the joy of music creation for budding intergalactic musicians of all skill levels. Set among the stars, you will strive to become the legendary Cosmic DJ through six musical adventures as you’re tasked with repairing the all-important “Jamtennas” and bring harmony back to the galaxy.', DATE('2014-10-29')) ON CONFLICT DO NOTHING;
