INSERT INTO games (id, name, summary, release) VALUES (19026, 'Crashed Lander', 'Outwit gravity, race the clock, explore new worlds. Crashed lander is a gravity-based arcade game inspired by classics like Lunar Lander and Space Taxi. You''ll pilot your craft from pad to pad trying to beat your best time – then flirt with that fine line between speed and control.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16327, 'Crasher', 'Crasher is a Multiplayer Online Battle Arena (MOBA) game that uses vehicles instead of human characters.', DATE('2011-01-27')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15389, 'Crashlands', 'Crashlands is an outlandish story-driven-action-adventure set in a world of hidden magic and high technology. Tame and milk alien beasts, craft resources into menacing weaponry, befriend the locals, and go toe-to-chin with a maniac. All in the name of ON-TIME DELIVERY !
