INSERT INTO games (id, name, summary, release) VALUES (19569, 'Crazy Pixel Streaker', 'A Fresh and Insane Brawler with Roguelike elements. Lead a fellowship of jackasses abducted by angry aliens and forced to spread chaos into the most famous sports events. Instant fun, Retro Arcade style, for up to 4 players.', DATE('2016-03-01')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17214, 'Crazy Plant Shop', 'Become the ultimate botanist by running your own plant shop full of wacky and exotic plants. Whimsical clientele place orders for plants in your catalog, but not all plants you can order have the right traits your customer is looking for in a specimen. It’s up to you to use a special machine in your shop to breed the correct plant with the right traits to complete a customer’s order. Earn gold for each completed order and participate in the World Fair by submitting your best plant specimens. Use your gold to earn upgrades for your shop, and unlock all the species in Crazy Plant Shop to win the game!', DATE('2014-06-30')) ON CONFLICT DO NOTHING;
