INSERT INTO games (id, name, summary, release) VALUES (17105, 'Crea', 'Furthering the emerging sandbox genre, Crea features combat and crafting in randomly generated, fantasy-style worlds for you to explore, tear apart and build back up. Crea differentiates itself in many ways such as RPG-style character progression, several unique systems, extensive mod support and a charming art style.', DATE('2016-06-08')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15035, 'CreaVures', 'Take control of five cute, mystical creatures of miniature proportions called CreaVures.  CreaVures always travel in pairs, and they must use their special abilities to help each other solve interesting platforming puzzles.  Set in a world with beautiful phosphorescent colors, these tiny creatures journey to save their homeland and restore light to their dying forest.', DATE('2011-01-02')) ON CONFLICT DO NOTHING;
