INSERT INTO games (id, name, summary, release) VALUES (23980, 'Crusader Kings II: Sunset Invasion', 'Addition or additional script adds a fictional invasion of bloodthirsty Aztecs in Europe. This is the first Paradox Development Studios, where they have departed from the true story.', DATE('2012-11-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22660, 'Crusader Kings II: Sword of Islam', 'Provides an opportunity to play for the Muslim dynasty, also adds to them new kinds of intrigue and various features. Muslims can have one home to his wife and three secondary. Half of the main skills of the wife goes to the governor.', DATE('2012-06-25')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22663, 'Crusader Kings II: The Old Gods', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23850, 'Crusader Kings II: The Reaper''s Due', 'Little remains of those settlements where the Black Death has passed through. Hasty burials make a mockery of the sacraments and set a feast for the crows. Once prosperous provinces have been laid waste, and the lord of the land hides away – secluded in his hilltop fortress, hoping the plague will pass him by. Wiser princes took precautions, and didn’t waste the revenues from more prosperous years.
