INSERT INTO games (id, name, summary, release) VALUES (17256, 'Cruxata', 'Cruxata was a web MMOG based on medieval wars during the crusades, a series of intermittent military campaigns sanctioned by various Popes in the Middle Ages. The game was created by an independent game developer know with the nickname "lucky". The game was recently closed due to lack of time to maintain and update.', DATE('2008-11-23')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3084, 'Cry of Fear', 'Cry of Fear is a single-player, horror modification for Half-Life 1 that brings you all the horrors you have always been afraid of.
INSERT INTO games (id, name, summary, release) VALUES (2967, 'Cryostasis', 'Year 1968. Arctic Circle, Russian North Pole station "Pole 21". The main hero Alexander Nesterov is a meteorologist accidentally caught inside an old nuclear ice-breaker North Wind, frozen in the ice desert many years ago. This steel creature once fought for its life and freedom, but it has fallen into an ice trap and all the creatures on its board lost not only their natural look, but a right to die. Cryostasis is a story about a drama of a Captain and his Ship evolving in the atmosphere of animal fear.', DATE('2009-02-26')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20737, 'Crypt Killer', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15512, 'Crypt Of Medea', 'Your courage and problem-solving ingenuity will be put to the test as you try to escape from Medea''s forbidden tomb. You must pass through countless rooms and secret passages. Overwhelming terror and ghoulish obstacles await you at every turn. You''ll need every shred of intelligence and survival skills to pass untouched from the Crypt of Medea. Escape has never been so thrilling!
INSERT INTO games (id, name, summary, release) VALUES (7886, 'Crypt of the NecroDancer', 'Crypt of the NecroDancer is a hardcore roguelike rhythm game. Can you survive this deadly dungeon of dance, slay the NecroDancer, and recapture your still beating heart? Or will you be a slave to the rhythm for all eternity?', DATE('2014-07-29')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26613, 'Crypt of the Necrodancer: Amplified', null, null) ON CONFLICT DO NOTHING;
