INSERT INTO games (id, name, summary, release) VALUES (17893, 'Crystal Catacombs', 'Crystal Catacombs is a challenging, retro inspired “Megavanian” love letter that utilizes Rogue-like procedurally generated levels/enemies/items with a heavy emphasis on classic 2D platforming. Discover, equip, use, and master an assortment of varied weapons, magic spells and upgrades.', null) ON CONFLICT DO NOTHING;
