INSERT INTO games (id, name, summary, release) VALUES (17372, 'CubeGun', 'A Sandbox Multiplayer Airship Shooting Game.Design battleships with weapons in a "rock paper scissors" mechanic.Shoot or Collide enemy ships into pieces.Challenge players all over the world with ships designed by yourself!.', DATE('2014-04-02')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17658, 'CubeZ', 'CubeZ is an action game with destroy the brain to kill mechanics, fully destructible characters, and a high level of customization that gives you the freedom to play with your own style. From tanked out battle axe warriors to SMG ninja assassins, everyone gets to play how they want.', DATE('2014-10-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25961, 'Cubemash', 'A unique isometric arcade color / pattern matching game for mobile. Cubemash combines elements of a rapid puzzle solver, a high energy arcade, and the essence of a casual game. Currently in Beta, the game is set to be released very soon in December 2016 on Android, and later on iOS.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16346, 'Cubemen', 'Cubemen is a fast paced, action packed, original 3D Tower Defense game that mixes TD with RTS in single player &amp; online multi-­player awesomeness! Get ready for some crazy Cubemen action! It’s the age old struggle between Good vs Bad, Blue vs Red, Little men vs Little men. Use your own little Cubemen to defend your base from other little Cubemen that are trying to run it over. It’s the usual story, but with many very interesting twists.', DATE('2012-02-04')) ON CONFLICT DO NOTHING;
