INSERT INTO games (id, name, summary, release) VALUES (17431, 'Cyto', 'Cyto has lost his memories, help him get them back. Fling Cyto through beautiful, other-worldly levels to collect memory fragments and piece together his story in this irresistible, cute, and challenging puzzle game.', DATE('2014-06-03')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12560, 'Cytron', 'A middle secret research center, designed to carry out shady experiments, is being viciously attacked by its robotic workforce. The mutinous mechanoids are assaulting the scientists that work in the center and releasing the experiments. The whole complex and its entire human staff will be wiped out in hours... if you don''t save them.
