INSERT INTO games (id, name, summary, release) VALUES (27026, 'Cytus', '200 songs and 400 variations, including many from famous composers around the world. Beautiful hand-drawn art style. Easy, intuitive Active Scan Line system and 3 types of notes. Different display modes allow player-friendly previewing of notes. Strong beat and rhythms provide satisfying feedback for taps. More than 9 difficulty levels for more fun and challenges. Various music genres: POP, JAZZ, TRANCE, HARDCORE, DRUM ''N BASS and much more.', DATE('2012-01-11')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4319, 'D', 'The first entry in the D series and one of the first games released by the company, it dealt with taboo content such as violence and cannibalism, featured 3D CGI full-motion video and a first-person perspective, It was the first game to star the digital actress, Laura.', DATE('1995-03-31')) ON CONFLICT DO NOTHING;
