INSERT INTO games (id, name, summary, release) VALUES (4758, 'D4: Dark Dreams Don''t Die - Season 1', 'A time traveling detective has to solve his wife’s murder before he can prevent it. Use his supernatural ability to “dive” into the world of the living past by touching artifacts called “mementos” to find out the truth behind his wife’s death.', DATE('2014-09-18')) ON CONFLICT DO NOTHING;
