INSERT INTO games (id, name, summary, release) VALUES (27387, 'DYSTORIA', 'Master the art of six-axis piloting as you blast your way through a mind-bending space labyrinth to a hypnotic synthwave soundtrack and retrofuturistic style.', DATE('2017-02-20')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24461, 'Daath Origins', 'Take command of an interstellar starship and guide her through star systems, planets, wormholes, dimensions, alternate timelines, and more, all the while managing a crew with almost 1 million fragile human lives to protect or exploit, in this open and deep turn-based RPG. 
INSERT INTO games (id, name, summary, release) VALUES (25484, 'Dachinko', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17691, 'Daedalus - No Escape', 'Ever wanted to play Unreal tournament or Quake 3 Arena from a top down 2D perspective ? Daedalus - No Escape brings you exactly that, the same feel and control as in an arena FPS but from top down, in a dark sci-fi mood inspired by Alien Breed.', DATE('2014-09-01')) ON CONFLICT DO NOTHING;
