INSERT INTO games (id, name, summary, release) VALUES (17032, 'Darkwood', 'Darkwood is a unforgiving survival horror with a procedurally generated, open world. By blending RPG, roguelike and adventure elements together with a challenging difficulty, Darkwood aims to please players craving for a deep and rewarding experience.
