INSERT INTO games (id, name, summary, release) VALUES (26166, 'Dauntless', null, DATE('2017-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11389, 'Dave Goes Nutz!', 'Life is difficult enough without having Dr. Nemesis as your arch-villain. Why does he have to pick on Dave’s little brother, Delbert, so much? Not only does he snatch the already kidnapping-prone Delbert, but he hides him away deep in the bowel of an insane asylum! Crazy nurses wearing purple of all colors wander the halls, cleaver wielding orderlies, and jolts of electricity all stand in Dangerous Dave’s way in Dave Goez Nutz!', DATE('1993-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4794, 'Dave Mirra BMX Challenge', null, DATE('2006-11-01')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26012, 'Dave Mirra Freestyle BMX', 'Dave Mirra Freestyle BMX is a video game released in 2000 featuring BMX Rider Dave Mirra and other professional BMX riders. It was developed by Z-Axis Ltd. and published by Acclaim Entertainment under their Acclaim Max Sports label. The game was released on the PlayStation, Game Boy Color, Dreamcast and Microsoft Windows. The player''s main objective in the game is to choose one of the riders and work their way through a total of 12 different levels, completing objectives to unlock new equipment. A sequel was released in 2001 entitled Dave Mirra Freestyle BMX 2.', DATE('2000-09-25')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3871, 'Dave Mirra Freestyle BMX 2', 'Ride around big open levels on your bmx bike, earning points for tricks. Complete various missions which help you unlock more levels and riders.', DATE('2001-08-26')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (6368, 'Dave Mirra Freestyle BMX 3', null, DATE('2002-11-25')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10119, 'David', 'You play David, an awfully small abstracted chosen hero tossed into a blissfully simple world overrun by giant culminations of evil physics-beasts. As David, you are given a slingshot-esque gift that you will have to use to conquer your colossus foes. Hold, aim, and fling to attack (Angry Birds style!).', null) ON CONFLICT DO NOTHING;
