INSERT INTO games (id, name, summary, release) VALUES (19721, 'Dawn of Heroes', 'Dawn of Heroes is a tactical role playing game that combines classic RPG concepts-including character development, epic combat, quests and puzzles-with a humorous style.', DATE('2010-09-12')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11024, 'Dawn of Magic', 'Dawn of Magic is a third-person RPG where the weapon of choice is magic. Unlike any other Action RPG, player characters in Dawn of Magic are all of one type: Mage. However, with dozens of different fields of magic available, players will have plenty of opportunities to customize their characters and specialize in the magic of their choice. The game is set in a richly detailed, medieval fantasy world, allowing the player a wide variety of choices both in individual quests and character development.', DATE('2007-04-26')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11025, 'Dawn of Magic 2', 'The well-known Action-RPG Dawn of Magic will now be continued as the evil forces won''t leave the world without a desperate struggle after the death of the monstrous immortal Modo.
