INSERT INTO games (id, name, summary, release) VALUES (24021, 'Dawn of the Plow', 'In Dawn of the Plow you work as the local snow plow driver. The latest blizzard has hit town and the roads are getting covered with snow. You must clear out the streets so the residents can get home.', DATE('2015-11-02')) ON CONFLICT DO NOTHING;
