INSERT INTO games (id, name, summary, release) VALUES (21080, 'Deadliest Warrior: Legends', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2354, 'Deadlight', 'The world has ended. There is no hope. No new beginning. Only the survivors. DEADLIGHT follows the journey of Randall Wayne, a man searching for his family across Seattle during the aftermath of a 1980s event that has decimated life on earth. This visually stunning Cinematic Survival Platformer will challenge you to run, jump, climb, and struggle for your life as you look for answers and the ones you love.', DATE('2012-07-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19454, 'Deadlight: Directors Cut', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24532, 'Deadlight: Director’s Cut', null, null) ON CONFLICT DO NOTHING;
