INSERT INTO games (id, name, summary, release) VALUES (26822, 'Deadlings: Rotten Edition', null, DATE('2014-11-10')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10172, 'Deadlock II: Shrine Wars', 'Deadlock II: Shrine Wars is an exciting game of strategy, resource management and military conquest. Each race has its own strengths and weaknesses - learn to exploit them to your advantage. Build, trade, research, attack, as you search for the ancient knowledge that will grant you ultimate control over the entire Universe.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10171, 'Deadlock: Planetary Conquest', 'In Deadlock, an intense turn-based strategic simulation, seven races colonize a virgin planet, but only one will survive. Build your province’s structures. Conduct research to advance your race. Devise the perfect strategy of diplomacy, intimidation and warfare. Every decision you make will determine the ultimate fate of your people, your empire, and your survival.', DATE('1996-07-21')) ON CONFLICT DO NOTHING;
