INSERT INTO games (id, name, summary, release) VALUES (19873, 'Death Goat', 'Death Goat is a bloody arcade shooter featuring hordes of brutal enemies &amp; eardrum destroying Heavy Metal.', DATE('2016-06-06')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4801, 'Death Jr. II: Root of Evil', 'Teaming up for even more firepower, DJ and Pandora will take on a variety of possessed enemies as they progress through an adventure that seamlessly blends smooth platforming, intense combat, complex enemy AI and a unique sense of humor.', DATE('2006-10-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21412, 'Death Jr. and the Science Fair of DOOM', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12041, 'Death Knights of Krynn', 'Even after defeating Myrtani and putting an end to the draconian threat, evil still lurks in Krynn. Your daring group of adventurers now face a far greater danger...the threat of the dreaded lord Soth and his growing undead army, plotting to destroy all that is good in Krynn.', DATE('1991-12-30')) ON CONFLICT DO NOTHING;
