INSERT INTO games (id, name, summary, release) VALUES (16204, 'Delve Deeper', 'Delve Deeper is a turn-based Adventure/Strategy game for Windows that combines HD pixel art, tile placement, and RPG elements in a bright and humorous atmosphere. Up to four players take command of Dwarf mining teams in this fast and furious dungeon adventure meets devious puzzler, building the map as you play for an ever-changing, endlessly replayable strategy experience for you and your three best friends, or three devious AI teams.', DATE('2010-09-23')) ON CONFLICT DO NOTHING;
