INSERT INTO games (id, name, summary, release) VALUES (11448, 'Dengeki Bunko: Fighting Climax', 'Dengeki Bunko: Fighting Climax is a 2D arcade fighting game developed to celebrate the 20th anniversary of ASCII Media Works'' Dengeki Bunko imprint, featuring various characters from light novels published under the imprint. The game was initially launched in Japanese arcades and was later ported to home consoles.', DATE('2014-03-17')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13550, 'Dengeki Bunko: Fighting Climax Ignition', 'Dengeki Bunko: Fighting Climax Ignition is a Japanese fighting game based on various Dengeki Bunko light novel properties and an updated version of the original Dengeki Bunko: Fighting Climax. The update contains additional characters and more balanced gameplay.', DATE('2015-07-27')) ON CONFLICT DO NOTHING;
