INSERT INTO games (id, name, summary, release) VALUES (17187, 'Derrick the Deathfin', 'Enter an irreverent aquatic world full of paper, goofy fish, paper, arcade action, paper, wanton destruction, and paper. Join Derrick on his epic quest to decimate everything he sees under the guise of avenging the death of his parents!', DATE('2014-04-30')) ON CONFLICT DO NOTHING;
