INSERT INTO games (id, name, summary, release) VALUES (11870, 'Desert Falcon', 'Desert Falcon is a 3D (isometric) flying shooter game that was developed and published in 1987 by Atari. The game was released for the Atari 2600, 7800, and XE Game Systems of the Atari 8-bit family.', DATE('1987-06-13')) ON CONFLICT DO NOTHING;
