INSERT INTO games (id, name, summary, release) VALUES (7260, 'Desperados: Wanted Dead Or Alive', 'Play six Desperados, lead by the hero John Cooper in the hardest and wildest Wild West Game of all times. Guide Cooper and his fearless companions in the exciting pursuit of the mysterious bandit leader El Diabolo.
INSERT INTO games (id, name, summary, release) VALUES (18516, 'Desperate Housewives: The Game', 'The official game of the TV series produced by ABC Studios. The game is developed by Liquid Entertainment and published by Buena Vista Games.', DATE('2006-10-04')) ON CONFLICT DO NOTHING;
