INSERT INTO games (id, name, summary, release) VALUES (5591, 'Deus Ex: The Fall', 'This is a port of the original DEUS EX: THE FALL experience, first released on iOS and Android in 2013 – action, stealth, hacking, and social gameplay. A prequel story to Deus Ex: Human Revolution that builds on the DEUS EX universe. Fight for survival in a global conspiracy.', DATE('2013-07-10')) ON CONFLICT DO NOTHING;
