INSERT INTO games (id, name, summary, release) VALUES (16636, 'Dino Run DX', 'DINO RUN DX is an ongoing evolution of the classic prehistoric multiplayer racing game. Control your iconic raptor through an endless assortment of procedurally generated landscapes and outrun the ever-approaching WALL OF DOOM.  Will you end up one of the millions of doomsday casualties, or will you ESCAPE EXTINCTION?', DATE('2015-08-01')) ON CONFLICT DO NOTHING;
