INSERT INTO games (id, name, summary, release) VALUES (4806, 'Disney Channel All Star Party', null, DATE('2010-10-25')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17758, 'Disney G-Force', 'Based on the Walt Disney Pictures and Jerry Bruckheimer blockbuster movie of the same name, G-Force the video game is an exciting action-platformer that draws you into the adventures of an elite team of trained guinea pigs on a mission to thwart a sinister plot to destroy the world. Take control of both G-Force commander Darwin and his housefly surveillance sidekick, Mooch, as they apply their talents to defeat an evil army of altered household appliances.
INSERT INTO games (id, name, summary, release) VALUES (19212, 'Disney Golf', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2926, 'Disney Infinity', 'DISNEY INFINITY unlocks the freedom to play with some of your favorite Disney and Disney/Pixar worlds like never before! You can experience adventures in the worlds of Pirates of the Caribbean, The Incredibles and Monsters University - take on the role of Sulley, the naturally gifted ‘scarer’; become Captain Jack Sparrow, the sword-wielding pirate; or transform yourself into Mr. Incredible, one of the world''s greatest crime-fighters. Battle enemies, solve puzzles, overcome obstacles and complete a variety of other unique quests. Or create your own world! Unlock virtual toys, characters, buildings, weapons, gadgets and more – and bring them into the Disney Infinity ‘Toy Box’ where you can mix them all up to create your own game. In the Disney Infinity Toy Box, there are no rules and you can create any adventure you want. Share your creations with your friends with up to 4-player co-op play. The more you play, the more you unlock so the story never ends. It’s up to you and your imagination – infinite possibilities, infinite ways to inspire your imagination!', DATE('2013-08-17')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17818, 'Disney Infinity 2.0: Marvel Super Heroes', 'In Disney Infinity (2.0 Edition) you have the freedom and endless opportunity to create stories and play experiences starring some of your favorite Marvel Super Heroes and Disney characters. Save the World or Create your Own!', DATE('2014-10-04')) ON CONFLICT DO NOTHING;
