INSERT INTO games (id, name, summary, release) VALUES (6756, 'Disney Magical World', 'Disney Magical World features dozens of Disney characters in a variety of Disney-themed worlds. Players can customize their characters with hundreds of Disney-themed outfits and accessories, all while managing a café, planting crops and collecting Disney character cards. Players help different characters solve problems or find missing items.', DATE('2013-07-31')) ON CONFLICT DO NOTHING;
