INSERT INTO games (id, name, summary, release) VALUES (18364, 'Divide', 'Divide is a character-driven, science fiction action/adventure game with a modern take on isometric adventures of the past. Search for clues that reveal new insight into the story, interact with fully realized characters, and combat enemies by hacking out of sight, or launching a direct assault.', DATE('2017-01-30')) ON CONFLICT DO NOTHING;
