INSERT INTO games (id, name, summary, release) VALUES (1254, 'DmC: Devil May Cry', 'In this retelling of Dante''s origin story which is set against a contemporary backdrop, DmC Devil May Cry™ retains the stylish action, fluid combat and self-assured protagonist that have defined the iconic series but inject a more brutal and visceral edge.', DATE('2013-01-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21024, 'DmC: Devil May Cry - Vergil''s Downfall', 'Aside from playing as Vergil throughout the content, Vergil''s Downfall features additional hidden items to collect, two new enemies and a new boss, and has a leaderboard with which to compete with others. The content also comes with four difficulty levels, new combos, and new locations.
