INSERT INTO games (id, name, summary, release) VALUES (19546, 'DropZone', '"Dropzone for PC is a Free-to-Play-Game that combines intense action with deep strategic planning and customization. Players control squads of three mighty war machines in an explosive 15-minute battle for dominance. Powered by the Sparkle Engine, a game engine built from the ground up for competitive online play, Dropzone is the Next Generation of RTS."
