INSERT INTO games (id, name, summary, release) VALUES (17219, 'Duet', 'Your survival is dependent on protecting two vessels - they are devices in sync, a dance and song between two entities tethered together in symbiosis. Feel edge of your seat terror where the world around you becomes quiet and numb as all that matters is the game -- that is Duet.', DATE('2013-10-09')) ON CONFLICT DO NOTHING;
