INSERT INTO games (id, name, summary, release) VALUES (18495, 'Duke It Out In D.C.', 'Duke It Out In D.C. is an expansion pack to the popular first-person shooter, Duke Nukem 3D, developed by Sunstorm Interactive and published by WizardWorks. The add-on was released on March 17, 1997.', DATE('1997-03-16')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (957, 'Duke Nukem', 'Duke Nukem is the debut of the world’s greatest wise-cracking, alien-punching, gun-toting badass - a man who would later go on to combine both ass-kicking and bubblegum in a revolutionary way. In a world ravaged by Dr. Proton’s villainous ambitions, the original video game action hero must rise against the evil scientist’s army of sinister Techbots. And although Duke isn’t exactly what the Doctor ordered, he’s exactly what the world needed.', DATE('1991-06-30')) ON CONFLICT DO NOTHING;
