INSERT INTO games (id, name, summary, release) VALUES (8505, 'Duke Nukem: Manhattan Project', 'Duke Nukem takes his Mighty Boot to the Big Apple. Using GLOPP (Gluon Liquid Omega-Phased Plasma), the maniacal mech Morphix has transformed the creatures of New York City into an army of bloodthirsty mutants. In Duke Nukem: Manhattan Project, Duke must deploy WIN (Weapons, Insults and Name-Calling) to rid the city of goo-based bad guys, and return peace and tranquility to The City That Never Sleeps.', DATE('2002-05-13')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4130, 'Duke Nukem: Time to Kill', 'Duke Nukem: Time to Kill is a third-person shooter developed by n-Space for the PlayStation.', DATE('1998-09-29')) ON CONFLICT DO NOTHING;
