INSERT INTO games (id, name, summary, release) VALUES (23882, 'Dungelot: Shattered Lands', 'Dungelot: Shattered Lands is a Minesweeper-esque roguelike, packed with gurgling enemies, wicked mushrooms, angry totems, and a quite insane zombie cow. What what, Dungelot?', DATE('2016-02-17')) ON CONFLICT DO NOTHING;
