INSERT INTO games (id, name, summary, release) VALUES (15317, 'Dungeon Party', 'Dungeon Party is a free online game that is played in teams. You are plunged into a totally crazy, heroic-fantasy world, where you compete in teams of heroes in dungeons full of deadly traps and awesome monsters to win the treasures they are trying so hard to hide.
