INSERT INTO games (id, name, summary, release) VALUES (27080, 'Dynasty Feud', 'Dynasty Feud is a competitive, team based, fast-paced 2D brawler for 2-4 players. Fight with 40 characters divided in 8 dynasties belonging to different historical civilizations and cultures composed by five members. One-hit-kill system, multiple fighting styles and Warden Spirits.', null) ON CONFLICT DO NOTHING;
