INSERT INTO games (id, name, summary, release) VALUES (9206, 'Eador. Masters of the Broken World - Allied Forces', 'Allied Forces adds 14 brand new units to Eador. Masters of the Broken World. Each of the seven races in Eador is now reinforced with two additional units which have their unique skills and abilities. Choose your allies wisely to get the most of your combat potential.', DATE('2014-06-04')) ON CONFLICT DO NOTHING;
