INSERT INTO games (id, name, summary, release) VALUES (18726, 'Eisenhorn: XENOS', '‘Eisenhorn: XENOS'' is a premium 3D action and adventure game based on ‘Xenos'', the first book in Games Workshop''s best-selling ‘Eisenhorn'' trilogy, written by award winning author Dan Abnett. Set in the grim, dark future of Warhammer 40,000.
