INSERT INTO games (id, name, summary, release) VALUES (26022, 'Eiyuu Senki GOLD', 'Eiyuu*Senki GOLD is an improved version of Eiyuu*Senki for PC and was only released in Japan. It uses the original characters in a new story and adds new characters, an item creation system, and an infinite dungeon.', DATE('2014-03-27')) ON CONFLICT DO NOTHING;
