INSERT INTO games (id, name, summary, release) VALUES (16729, 'Elder Sign: Omens', 'Elder Sign: Omens is a digital version of the popular board game by Fantasy Flight Games. Build a team of unique investigators and fight to keep an all-powerful Ancient One from invading our world! Explore museums, oceans, deserts and more for the arcane Elder Signs that will help seal the Ancient One away.', DATE('2011-03-10')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17350, 'Eldevin', 'Eldevin is the award winning indie story-driven Free to Play MMORPG (Massively Multiplayer Online Role Playing Game) from Hunted Cow Studios. Join thousands of other players and explore the mystical lands of Eldevin. Featuring more than 160 realms, each filled with unique creatures and characters for you to encounter. Explore vast open plains or descend underground into hidden caves and dungeons, making friends or foes of the various factions as you carry out your epic quest to defend Eldevin Kingdom.', DATE('2014-10-02')) ON CONFLICT DO NOTHING;
