INSERT INTO games (id, name, summary, release) VALUES (17606, 'Ensign-1', 'Ensign-1 is a space combat game that gives players the freedom to leave their fighter to board ships, man turrets, and hijack alien fighters.', DATE('2014-10-03')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2538, 'Enslaved: Odyssey to the West', 'Enslaved: Odyssey to the West is an action-adventure platform video game developed by Ninja Theory and published by Namco Bandai Games. It was released on PlayStation 3 and Xbox 360 on October 5, October 7 and October 8, 2010 in North America, Australia, Japan and Europe respectively.[1][2] A premium version, featuring all DLC content, was made for Microsoft Windows and Sony Playstation 3 and was later released on October 25, 2013.
