INSERT INTO games (id, name, summary, release) VALUES (24192, 'Enslaved: Odyssey to the West Premium Edition', 'Follow a gripping, surprise-filled journey as two dissimilar characters form an uneasy partnership in order to survive through a perilous, post-apocalyptic America. 150 years in the future, war and destruction have left the world in ruins with few humans remaining and nature having reclaimed the world.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13201, 'Entaku no Seito', 'The Title is mostly referred to as "Students of the Round" in English instead of a more direct translation because an English version of the game was never made and the name is a better match for the English subtitle on the cover.
