INSERT INTO games (id, name, summary, release) VALUES (11339, 'Entanglement', 'Entanglement is a tile-placement puzzle game. The player tries to make the longest path possible by rotating and placing hexagonal tiles etched with paths to extend a path without running into a wall.', DATE('2010-03-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11182, 'Enter the Gungeon', 'Enter the Gungeon is a gunfight dungeon crawler following a band of regretful misfits seeking to shoot, loot, dodge roll and table-flip their way to personal absolution by reaching the legendary Gungeon’s ultimate treasure: the gun that can kill the past. Select a hero and battle your way to the bot¬tom of the Gungeon by surviving a challenging and evolving series of floors filled with the dangerously adorable Gundead and fearsome Gungeon bosses armed to the teeth. Gather precious loot, discover hidden secrets, and chat with opportunistic merchants and shopkeepers to purchase powerful items to gain an edge.', DATE('2016-04-04')) ON CONFLICT DO NOTHING;
