INSERT INTO games (id, name, summary, release) VALUES (26553, 'Epic Manager - Create Your Own Adventuring Agency', '&quot;Epic Manager offers a colorful mix of roster management and character progression found in titles like Football Manager or Final Fantasy Tactics, narrative choices &amp; consequences in the beloved tradition of the gamebooks series Choose Your Own Adventure, the epic scope of a D&amp;D campaign or a Lord of the Rings novel and the humorous tone of the Princess Bride, all in one awesome package!&#13;
