INSERT INTO games (id, name, summary, release) VALUES (16576, 'Epigenesis', 'Epigenesis is an online multiplayer sports/FPS game in which two sides are locked in a never-ending fight for world domination by playing ball. In this non-lethal ballgame of the future, players leap across platforms suspended high up in the air trying to get a ball located in the middle of the arena and score goals against their opponents. Each player carries a gravity cannon capable of pushing enemies (and friends) down from the heights of the arena.', DATE('2013-10-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11657, 'Epistory', 'Epistory is an atmospheric adventure typing game that tells the story of a writer lacking inspiration who asks her muse to help write her latest book.
