INSERT INTO games (id, name, summary, release) VALUES (26011, 'Eradicated', 'Eradicated is a modern era survival game. After waking up to a world of disaster, apocalypse, and the zombutated creatures that lye around the world, your job is to uncover the truth of this horrifying eradication while facing off against your opponents in this apocalypse wasteland.', DATE('2017-07-04')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8340, 'Eradicator', 'Explode into action! Disable an automated fortress that has been mysteriously reactivated. It''s up to you to eradicate the hordes of destructive, cunning enemies.', DATE('1996-09-29')) ON CONFLICT DO NOTHING;
